mapper.insert:
INSERT INTO product_material
(
	product_material_code
	, pd_code
	, rawm_code
	, product_material_amount
)
VALUES
(
	 'pm' || TO_CHAR(product_material_code.NEXTVAL)
	, #{pd_code}
	, #{rawm_code}
	, #{product_material_amount}
)

mapper.update:
		update product
		set
			pd_name = #{pd_name}
 			,pd_rmcost = #{pd_rmcost}
 			,pd_manufcost = #{pd_manufcost}
			,pd_sort = #{pd_sort}
		WHERE pd_code=#{pd_code}
