mapper.insert:
insert into manufacture ( mfseq, pd_code, manuf_startdate, manuf_status, warehouse_code, manuf_amount, manuf_requserid, manuf_reqdate, mf_delete
) values ( 'm' || TO_CHAR(mfseq.nextval), #{pd_code}, to_char(to_date(#{manuf_startdate},'MM/DD/YYYY'),'YYYY-MM-DD'), '미완', #{warehouse_code}, #{manuf_amount}, #{manuf_requserid}, sysdate, 'N'
)

mapper.update:
update
	product
set
	pd_delete=#{pd_delete}
where
	pd_code=#{pd_code}

		update
 			 manufacture	
		set
			mf_delete=#{mf_delete}
		where
			mfseq=#{mfseq}

