mapper.insert:
INSERT INTO RAWMATERIAL
(
	rawm_code
	,supplierseq
	,rawm_name
	,rawm_sort
	,rawm_price
	,rawm_autoorder
	,rawm_minimum
	,rawm_set
)
VALUES
(
	rawm_code.nextval
	,#{supplierseq}
	,#{rawm_name}
	,#{rawm_sort}
	,#{rawm_price}


	,'CHECKED'


	,'UNCHECKED'


	,#{rawm_minimum}
	,#{rawm_set}
)

mapper.update:
update
	RAWMATERIAL
set
	supplierseq=#{supplierseq}
	,rawm_name=#{rawm_name}
	,rawm_sort=#{rawm_sort}
	,rawm_price=#{rawm_price}
	,rawm_autoorder=#{rawm_autoorder}
	,rawm_minimum=#{rawm_minimum}
	,rawm_set=#{rawm_set}
where
	rawm_code=#{rawm_code}
