mapper.insert:
INSERT INTO RAWMATERIAL
(
	rawm_code
	,supplierseq
	,rawm_name
	,rawm_sort
	,rawm_price
	,rawm_notice
	,rawm_minimum
	,rawm_delete
)
VALUES
(
	 'm' || TO_CHAR(supplierseq.nextval)
	,#{supplierseq}
	,#{rawm_name}
	,#{rawm_sort}
	,#{rawm_price}


	,'CHECKED'


	,'UNCHECKED'


	,#{rawm_minimum}
	,#{rawm_delete}
)

mapper.update:
update
	RAWMATERIAL
set
	rawm_delete=#{rawm_delete}
where
	rawm_code=#{rawm_code}

update
	RAWMATERIAL
set
	supplierseq=#{supplierseq}
	,rawm_name=#{rawm_name}
	,rawm_sort=#{rawm_sort}
	,rawm_price=#{rawm_price}
	,rawm_notice=#{rawm_notice}
	,rawm_minimum=#{rawm_minimum}
where
	rawm_code=#{rawm_code}
