mapper.insert:
insert into supplier
(
	supplierSeq
	,supplierName
	,supplierOwner
	,supplierAddr
	,supplierTel
	,supplierPIC
	,supplierEmail
)
values
(
	supplierSeq.nextval
	,#{supplierName}
	,#{supplierOwner}
	,#{supplierAddr}
	,#{supplierTel}
	,#{supplierPIC}
	,#{supplierEmail}
)

mapper.update:
update
	supplier
set
	supplierName=#{supplierName}
	,supplierOwner=#{supplierOwner}
	,supplierAddr=#{supplierAddr}
	,supplierTel=#{supplierTel}
	,supplierPIC=#{supplierPIC}
	,supplierEmail=#{supplierEmail}
where
	supplierSeq=#{supplierSeq}
